import React, { useEffect, useState } from "react";
import axios from "axios";
import AssignmentIndIcon from "@mui/icons-material/AssignmentInd";
import BusinessIcon from '@mui/icons-material/Business';
import PanToolIcon from "@mui/icons-material/PanTool";
import HouseIcon from "@mui/icons-material/House";
import AccessibilityIcon from "@mui/icons-material/Accessibility";
import "../style/Cadastro.css";
import InputAdornment from "@mui/material/InputAdornment";
import InputCamera from "./InputCamera";
import TextField from "@mui/material/TextField";
import { styled } from "@mui/material/styles";
import { pink } from "@mui/material/colors";

const InputsIconNextPage = () => {
  
  
  const pesquisaCep = async (cep) => {
    const url = `https://viacep.com.br/ws/${cep}/json/`;
    const response = await fetch(url);
    const data = await response.json();
    return data;
  };

  const cepValido = (cep) => /^[0-9]{8}$/.test(cep);

  const limparFormulario = () => {
    document.querySelector("#moradia").value = "";
  };

  const preencherFormulario = async (evento) => {
    const cep = evento.target.value.replace('-', '');
    limparFormulario();
    if (cepValido(cep)){
        const infoCep = await pesquisaCep(cep);
        if (infoCep.erro){
            document.querySelector('#moradia').value = "CEP n√£o encontrado, tente novamente.";
        }else{
        document.querySelector('#moradia').value = infoCep.logradouro;
        }
    }else{
        document.querySelector('#moradia').value = "CEP invalido, por favor digite um cep valido!";
    }
};
  document.querySelector('#cep').addEventListener('focusout', preencherFormulario);
  

  return (
    <>
      <InputCamera />
      <div id="contInputsPet">
        <select id="InputsContainerSelect">
          <option disabled selected>
            Selecione um gen√™ro
          </option>
          <option>Masculino</option>
          <option>Femininio</option>
          <option>N√£o Binario</option>
        </select>
        <AssignmentIndIcon id="iconInputLabel" />
        <input
          label="CPF"
          id="cpf"
          className="containerInputs"
          placeholder="CPF"
        />
      </div>
      <div id="contInputsPetCheckbox">
        <div id="contInputCheckbox">
          <label className="labelInputCheckboxCuidador">
            Possui Animais
            <input type="checkbox" />
            <span className="checkmarkCuidador"></span>
          </label>
        </div>
        <div id="contInputCheckbox">
          <label className="labelInputCheckboxCuidador">
            Possui Crian√ßas?
            <input type="checkbox" />
            <span className="checkmarkCuidador"></span>
          </label>
        </div>
      </div>
      <div id="contInputsPet">
        <PanToolIcon id="iconInputLabel" />
        <input
          label="Prefer√™ncias"
          id="limitacoes"
          className="containerInputLimitacoes"
          placeholder="Voc√™ possui limita√ß√µes?"
        />
      </div>
      <div id="contInputsMoradia">
        <BusinessIcon id="iconInputLabel" />
        <input
          id="cep"
          className="containerInputMoradia"
          placeholder="Informe seu cep"
          type="number"
          required
        />
        <HouseIcon id="iconInputLabel" />
        <input
          label="Moradia"
          id="moradia"
          className="containerInputMoradia"
          placeholder="Aonde voc√™ reside?"
          required
        />
        <div>
          <AccessibilityIcon id="iconInputLabel" />
          <input
            label="Prefer√™ncias"
            id="preferencias"
            placeholder="Possui alguma prefer√™ncia?"
            className="containerInputPreferencias"
          />
        </div>
      </div>
    </>
  );
};

export default InputsIconNextPage;
