import { useState } from "react";
import axios from "react";
import AssignmentIndIcon from "@mui/icons-material/AssignmentInd";
import LocationOnIcon from "@mui/icons-material/LocationOn";
import LockIcon from "@mui/icons-material/Lock";
import LockOpenIcon from "@mui/icons-material/LockOpen";
import AccountCircle from "@mui/icons-material/AccountCircle";
import AlternateEmailIcon from "@mui/icons-material/AlternateEmail";
import CallIcon from "@mui/icons-material/Call";
import "../style/CadastroCliente.css";
import CameraAltIcon from "@mui/icons-material/CameraAlt";
import InputButtonCadastrar from "../button/inputButtonCadastrar";
import InputCamera from "./InputCamera";

const ConteudoInputsCliente = () => {
  const [user, setUser] = useState({
    name: "",
    email: "",
    senha: "",
    moradia: "",
    telefone: "",
    cpf: "",
  });

  const handleUserSubmit = (user) => {
    const url = ("http://localhost/Cuidador/Cliente/api/cliente", user);
    fetch.post(url).then((res) => {
      console.log(res.data);
    });
  };

  function validarSenhaForca() {
    var senha = document.getElementById("senha").value;
    var forca = 0;
    document.getElementById("impSenha");

    if (senha.length >= 4 && senha.length <= 7) {
      forca += 10;
    } else if (senha.length > 7) {
      forca += 25;
    }

    if (senha.length >= 5 && senha.match(/[a-z]+/)) {
      forca += 10;
    }

    if (senha.length >= 6 && senha.match(/[A-Z]+/)) {
      forca += 20;
    }

    if (senha.length >= 7 && senha.match(/[@#$%&;]+/)) {
      forca += 25;
    }

    exibirForca(forca);
  }

  function exibirForca(forca) {
    document.getElementById("impForcaSenha");

    if (forca >= 30 && forca < 50) {
      document.getElementById("erroSenhaForca").innerHTML =
        "<div class='barra-media'><span style='color: #FFD700'>Sua senha está média</span></div>";
    } else if (forca >= 50 && forca < 70) {
      document.getElementById("erroSenhaForca").innerHTML =
        "<div class='barra-forte'><span style='color: #008000'>Sua senha está forte</span></div>";
    } else if (forca >= 70 && forca < 90) {
      document.getElementById("erroSenhaForca").innerHTML =
        "<div class='barra-muito-forte'><span style='color: #90EE90'>Sua senha está muito forte</span></div>";
    } else if (forca >= 20 && forca < 25) {
      document.getElementById("erroSenhaForca").innerHTML =
        "<div class='barra'><span style='color: #ff0000 '>Sua senha está muito fraca</span></div>";
    }
  }

  return (
    <>
      <form onSubmit={() => handleUserSubmit(user)}>
        <div id="containerInput">
          <div id="containerBorderImage">
            <input
              type="file"
              name="inputImage"
              className="inputImage"
              id="foto"
              value={user.foto}
            />
            <CameraAltIcon id="iconInputCamera" />
          </div>
        </div>
        <div id="contInputsPet">
          <AccountCircle id="iconInputLabel" />
          <input
            label="Nome"
            id="nome"
            value={user.nome}
            className="containerInputNome"
            placeholder="Nome"
            onChange={(e) => setUser({ ...user, name: e.target.value })}
          />

          <input type="date" className="inputCalendar" />
        </div>
        <div id="contInputStretch">
          <AlternateEmailIcon id="iconInputLabel" />
          <input
            label="Email"
            id="email"
            value={user.email}
            className="containerInputEmail"
            placeholder="Email"
            onChange={(e) => setUser({ ...user, email: e.target.value })}
          />
        </div>
        <div id="contInputsPet">
          <LockIcon id="iconInputLabel" />
          <input
            label="Senha"
            id="senha"
            type="password"
            value={user.senha}
            onKeyUp={validarSenhaForca}
            className="containerInput"
            placeholder="Senha"
            onChange={(e) => setUser({ ...user, senha: e.target.value })}
          />

          <LockOpenIcon id="iconInputLabel" />
          <input
            label="Confirmar Senha"
            id="confirmarSenha"
            className="containerInput"
            placeholder="Confirmar Senha"
          />
        </div>
        <div id="impSenha"></div>
          <div id="impForcaSenha"></div>
          <div id="erroSenhaForca"></div>
        <div id="contInputsPet">
          <LocationOnIcon id="iconInputLabel" />
          <input
            label="Moradia"
            id="moradia"
            className="containerInput"
            value={user.moradia}
            placeholder="Localização"
            onChange={(e) => setUser({ ...user, moradia: e.target.value })}
          />
          <CallIcon id="iconInputLabel" />
          <input
            label="Telefone"
            id="telefone"
            value={user.telefone}
            className="containerInput"
            placeholder="Telefone"
            onChange={(e) => setUser({ ...user, telefone: e.target.value })}
          />
        </div>
        <div id="contInputsPet">
          <AssignmentIndIcon id="iconInputLabel" />
          <input
            label="CPF"
            id="cpf"
            value={user.cpf}
            className="containerInput"
            placeholder="CPF"
            onChange={(e) => setUser({ ...user, cpf: e.target.value })}
          />
        </div>

        <div id="containerButton">
          <input
            value="Cadastrar"
            type="submit"
            name="inputConfirmarSenha"
            id="buttonCadastrar"
            className="button"
          />
        </div>
      </form>
    </>
  );
};

export default ConteudoInputsCliente;
